  `customerId` bigint(20) NOT NULL default '0',
  `comment` text NOT NULL,
  `commentDate` varchar(16) NOT NULL default '',
  PRIMARY KEY  (`id`),
  KEY `newsId` (`newsId`),
  KEY `customerId` (`customerId`),
  KEY `commentDate` (`commentDate`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
